import { useState } from 'react';
import useSystem_Users from '../api/useSystem_Users';

const useLoginForm = (initial) => {
    const [ value, setValue ] = useState(initial);
    const [ userData, manageCredentials ] = useSystem_Users({ user: value.user, password: value.password, server: value.server, startingSesion: value.startingSesion });

    const handleChange = e => {
        setValue({
            ...value,
            [e.target.name]: e.target.value
        });
    }

    const startSesion = () => {
        console.log(userData);
        if(userData === undefined || userData.length === 0){
            setValue({
                ...value,
                ['credentialsFail']: true,
                ['buttonPushed']: true,
            });
        } else {
            setValue({
                ...value,
                ['credentialsFail']: false,
                ['buttonPushed']: false,
            });
            console.log('LOGIN OKKKK');
        }
    }

    return [ value, handleChange, startSesion, manageCredentials ];
}

export default useLoginForm;
