import { useState } from 'react';
import useSystem_Users from '../api/useSystem_Users';

const useLoginForm = (initial) => {
    const [ value, setValue ] = useState(initial);
    const [ userData, manageCredentials, startSesion ] = useSystem_Users({ user: value.user, password: value.password, server: value.server, startingSesion: value.startingSesion });

    const handleChange = e => {
        setValue({
            ...value,
            [e.target.name]: e.target.value
        });
    } 

    return [ value, handleChange, startSesion, manageCredentials ];
}

export default useLoginForm;
