import { useState } from 'react';
import useSystem_Users from '../api/useSystem_Users';

const useLoginForm = (initial) => {
    const [ value, setValue ] = useState(initial);
    const [ userData, changeCredentials ] = useSystem_Users({ user: value.user, password: value.password, server: value.server });
    const handleChange = e => {
        setValue({
            ...value,
            [e.target.name]: e.target.value
        });
    }
    const startSesion = () => {
        setValue({
            ...value,
            ['buttonPushed']: true
        });
    }
    return [ value, handleChange, startSesion ];
}

export default useLoginForm;
