import { useState } from 'react';

const callSystem_Users = async (server, user, password) => {
    let userData = undefined;
    if(user.length != 0 && password.length != 0) {
        const response = await fetch(`${server}/System_Users`, {
            headers: {},
            body: {
                "Windows_User": user,
                "Password": password
            }
        });
        userData = response.json();
    }

    return userData;
}

const useSystem_Users = ({ server, user, password }) => {
    const [value, setValue] = useState({ user: '', password: '', startingSesion: false});
    const changeCredentials = (credentials) => {
        setValue({
            ...value,
            ['user']: value.user,
            ['password']: value.password
        });
    }

    //Temp testing
    console.log(`User Length: ${value.user.length}
                 Password Length: ${value.password.length}`);

    const userData = callSystem_Users(server, user, password);

    console.log(`USER DATA: ${userData}`);

    return [ userData, changeCredentials ];
}

export default useSystem_Users;
