import { useState } from 'react';
import useSystem_Users from '../api/useSystem_Users';

const useLoginForm = (initial) => {
    const [ value, setValue ] = useState(initial);
    const [ userData, changeCredentials, handleStartingSesion ] = useSystem_Users({ user: value.user, password: value.password, server: value.server, startingSesion: value.startingSesion });

    const handleChange = e => {
        setValue({
            ...value,
            [e.target.name]: e.target.value
        });
    }

    const startSesion = () => {
        handleStartingSesion(true);
        changeCredentials({ user: value.user, password: value.password });

        if(userData != undefined){
            console.log('USER DATA:');
            console.log(userData);
        }
        setValue({
            ...value,
            ['buttonPushed']: true
        }); 
        //handleStartingSesion(false);
    }

    return [ value, handleChange, startSesion ];
}

export default useLoginForm;
