import { useState, useRef } from 'react';
import '../css/Login.css';

let buttonPushed = false;

const Login = () => {
    const [ value, setValue ] = useState({ user: '', password: ''});
    const inputName = useRef();
    const inputPassword = useRef();


    const handleChange = e => {
        setValue({
            ...value,
            [e.target.name]: e.target.value
        });

        console.log(value);
    }

    const startSesion = () => {
        buttonPushed = true;
    }
    return(
        <div>
            <h1>Login</h1>

            <div>
                <h2>Bienvenido</h2>

                <div>
                    <div>
                        <label>Usuario</label>
                        <input name='user' value={value.user} onChange={handleChange} ref={inputName}/>
                    </div>
                    <div>
                        <label>Contraseña</label>
                        <input name='password' value={value.password} onChange={handleChange} ref={inputPassword}/>
                        {buttonPushed && value.password.length === 0 ? <span>Debes ingresar una contraseña</span> : <span></span>}
                    </div>
                    <input type='submit' value='Iniciar Sesión' onClick={startSesion}/>
                </div>
            </div>
        </div>
    )
}

export default Login;
