import { useState } from 'react';

const useSystem_Users = ({ server, user, password, startingSesion }) => {
    console.log('RENDERING useSystem_Users');
    const [value, setValue] = useState({ server: server, user: user, password: password, startingSesion: startingSesion });
    let userData = undefined;
    const changeCredentials = (credentials) => {
        setValue({
            ...value,
            ['user']: credentials.user,
            ['password']: credentials.password,
            ['startingSesion']: credentials.startingSesion
        });
    }

    const handleStartingSesion = val => {
        console.log('VAAAALVAAAALLL',val);
        setValue({
            ...value,
            startingSesion: true
        });
        console.log('value.startingSesion',value);
    }

    //Temp testing
    //console.log(`User Length: ${value.user.length}
                 //Password Length: ${value.password.length}
                 //StartingSesion: ${value.startingSesion}`);

    if(value.startingSesion) {
        userData = callSystem_Users(value.server, value.user, value.password, value.startingSesion);
    }

    //console.log(`USER DATA: ${userData}`);

    return [ userData, changeCredentials, handleStartingSesion ];
}

const callSystem_Users = async (server, user, password, startingSesion) => {
    let userData = undefined;

    console.log('***********************************');
    console.log('DENTRO DE callSystem_Users');
    console.log(`SERVER: ${server}`);
    console.log(`USER: ${user}`);
    console.log(`PASSWORD: ${password}`);
    console.log(`STARTINGSESION: ${startingSesion}`);
    console.log('SALIENDO DE callSystem_Users');
    console.log('***********************************');

    if(user.length != 0 && password.length != 0 && startingSesion) {
        console.log('ENTRANDO EN EL IF');
        const response = await fetch(`${server}/System_Users`, {
            headers: {},
            body: {
                "Windows_User": user,
                "Password": password
            }
        });
        userData = response.json();
    }

    return userData;
}

export default useSystem_Users;
