import { useState } from 'react';

const useSystem_Users = ({ server, user, password, startingSesion }) => {
    console.log('RENDERING useSystem_Users');
    const [value, setValue] = useState({ server: server, user: user, password: password, startingSesion: startingSesion, userData: undefined });


    const manageCredentials = async (credentials) => {
        console.log('Ejecutando setValue 2');
        setValue({
            ...value,
            ['user']: credentials.user,
            ['password']: credentials.password,
            ['startingSesion']: credentials.startingSesion
        });
        if(value.startingSesion) {
            console.log('Ejecutando el if de startingSesion 3');
            setValue({
                ...value,
                ['userData']: await callSystem_Users(value.server, value.user, value.password, value.startingSesion),
                ['startingSesion']: false
            });
        }

        console.log('Ejecutando el console.log de userData el cual debe de ser una promesa 5', value.userData.json());
    }



    return [ value.userData, manageCredentials ];
}

//OTHER FUNCTION
const callSystem_Users = async (server, user, password, startingSesion) => {
    let userData = undefined;

    console.log('***********************************');
    console.log('DENTRO DE callSystem_Users');
    console.log(`SERVER: ${server}`);
    console.log(`USER: ${user}`);
    console.log(`PASSWORD: ${password}`);
    console.log(`STARTINGSESION: ${startingSesion}`);

    console.log('Ejecutando el fetch 4');
    if(user.length != 0 && password.length != 0 && startingSesion) {
        try{
            const response = await fetch(`${server}/System_Users/${user}/${password}`, {
                //headers: {
                    //'Content-Type': 'application/json',
                //},
                method: 'GET', 
                //mode: 'no-cors'
            });
            userData = response;
        } catch(error) {
            console.error(error);
        }
    }

    console.log('SALIENDO DE callSystem_Users');
    console.log('***********************************');

    return userData;
}

export default useSystem_Users;
