import { useState } from 'react';
import '../css/Login.css';

const Login = () => {
    const { value, setValue } = useState({ user: '', password: ''});

    const handleChange = e => {
        setValue({
            ...value,
            [e.target.name]: e.target.name
        });
    }
    return(
        <div>
            <h1>Login</h1>

            <div>
                <h2>Bienvenido</h2>

                <div>
                    <div>
                        <label>Usuario</label>
                        <input name='user' value={value.user} onChange={handleChange} />
                    </div>
                    <div>
                        <label>Contrase√±a</label>
                        <input name='password' value={value.password} onChange={handleChange} />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Login;
